import React from "react";
import '../../App.css';
import AdminDashboardNavBar from "../../components/AdminDashboardNavBar";
import { useEffect, useState } from 'react';
import Repository from "../../services/Repository"

const AllUsersPage = () => {

  const [users, setUsers] = useState([])
  const [titile, setTitle] = useState("")

  useEffect(() => {
    Repository.getAllUsers()
      .then(data => {
        setUsers(data);
        console.log(data);
      })
      .catch(error => {
        throw error;
      });
  }, []);

  const setAllData = () => {
    Repository.getAllUsers()
      .then(data => {
        setUsers(data);
        setTitle("All Users Details");
        console.log(data);
      })
      .catch(error => {
        throw error;
      });
  };

  const setStudentData = () => {
    Repository.getAllStudents()
      .then(data => {
        setUsers(data);
        setTitle("All Students Details");
        console.log(data);
      })
      .catch(error => {
        throw error;
      });
  };
  const setTeachertData = () => {
    Repository.getAllTeachers()
      .then(data => {
        setUsers(data);
        setTitle("All Teachers Details");
        console.log(data);
      })
      .catch(error => {
        throw error;
      });
  };


  return (

    <div>

      <AdminDashboardNavBar />
      <hr />

      <div style={{ marginTop: "5vw" }}>

        <div class="title-home-page"> <h1 class="wow slideInLeft" id="title">{titile}</h1> </div>

      </div>

      <hr />
      <div class="d-flex justify-content-center">
        <button class="btn btn-secondary d-inline-block mr-3" onClick={setAllData}>All Users</button>
        <button class="btn btn-primary d-inline-block mr-3" onClick={setStudentData}>All Students</button>
        <button class="btn btn-primary d-inline-block mr-3" onClick={setTeachertData}>All Teachers</button>
      </div>

      <div class="card-container">

        <ol>
          {users.map((user, index) => (
            <li key={index}>
              <div className="card" id="card-history" style={{ marginTop: "2vw", width: "50vw", float: "none" }}>
                <div className="student-details" style={{ marginTop: "2vw", marginBottom: "2vw" }}>
                  <ul>
                    <li><b>Username:</b> {user.username}</li>
                    <li><b>First Name:</b> {user.firstName}</li>
                    <li><b>Last Name:</b> {user.lastName}</li>
                    <li><b>Email:</b> {user.email}</li>
                    <li><b>Role:</b> {user.role}</li>
                    <li></li>
                  </ul>
                </div>
              </div>
            </li>
          ))}
        </ol>


      </div>

    </div>

  );
}

export default AllUsersPage;
