import React from "react";
import Repository from "../services/Repository";
import { Course } from '../components/Course';
import Navigationbar from "../components/NavigationBar";
import { useEffect, useState } from 'react';

export const MyCoursesPage = () => {

  const currentUser = localStorage.getItem('currentUser');
  const userRole = localStorage.getItem('userRole');

  const [courses, setCourses] = useState([]);
  const [isPending, setIsPending] = useState(true);

  useEffect(() => {
    if (userRole === "Student") {
      Repository.enrolledCourses(currentUser)
        .then(data => {
          setCourses(data);
          console.log(data);
          setIsPending(false);
        })
        .catch(error => {
          throw error;
        });
    }else if (userRole === "Teacher") {
      Repository.assignedCourse(currentUser)
      .then(data => {
        setCourses(data);
        setIsPending(false)
      })
      .catch(error => {
        throw error;
      });
    }

  }, []);

  return (
    <div>
      <Navigationbar />
      <hr />
      <div className="title-home-page" style={{ marginTop: "6vw" }} >
        <h1 className="wow slideInLeft" id="title">My Courses</h1>
      </div>
      <hr id="hr" />
      {isPending ? (
        <div className="d-flex justify-content-center">
          <div className="spinner-border" role="status" />
        </div>
      ) : (
        <div className="row">
          {course ? (
            <div className="col-12 col-sm-8 col-md-4">
              <div className="card">
                <div className="card-body">
                  <Course
                    id={0}
                    courseID={course.courseID}
                    courseName={course.courseName}
                    timeSlot={course.timeSlot}
                    aboutCourse={course.aboutCourse}
                    teacher={course.teacher}
                  />
                </div>
              </div>
            </div>
          ) : (
            courses.map((course, index) => (
              <div className="col-12 col-sm-8 col-md-4" key={course.courseID}>
                <div className="card">
                  <div className="card-body">
                    <Course
                      id={index}
                      courseID={course.courseID}
                      courseName={course.courseName}
                      timeSlot={course.timeSlot}
                      aboutCourse={course.aboutCourse}
                      teacher={course.teacher}
                    />
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
      )}
    </div>
  );
}
