import React from "react";
import Navigationbar from "../components/NavigationBar";
import '../CourseDetails.css';
import Repository from "../services/Repository";
import { useState, useEffect } from "react";

const MyCoursePage = () => {

    const currentUser = localStorage.getItem("currentUser");
    const token = localStorage.getItem("jwt");
    const [totalEnrollments, setTotalEnrollments] = useState("");

    const [isPending, setIsPending] = useState(true);
    const [courseName, setCourseName] = useState("");



    useEffect(() => {
        Repository.assignedCourse(currentUser, token)
            .then(data => {
                setCourseName(data.courseName);
                setCourseID(data.courseID);
                setTimeSlot(data.timeSlot);
                setAboutCourse(data.setAboutCourse);
                setTeacher(data.teacher);
            }).catch(error => {
                throw error;
            });

        Repository.courseEnrollments(courseID)
            .then(data => {
                setTotalEnrollments(data);
            }).catch(error => {
                throw error;
            }).finally(() => {
                setIsPending(false);
            });

    }, [currentUser, token, courseID]);

    return (
        <div>
            <Navigationbar />
            <hr />
            <div className="title-home-page" style={{ marginTop: "6vw" }} >
                <h1 className="wow slideInLeft" id="title">My Courses</h1>
            </div>
            <hr id="hr" />

            {isPending ? (
                <div className="d-flex justify-content-center">
                    <div className="spinner-border" role="status" />
                </div>
            ) : (
                <div className="Course-details" >
                    <div class="container d-flex justify-content-center">
                        <div class="c-card p-3 py-4" style={{ marginTop: "8vw" }}>
                            <div class="text-center">
                                <h3 class="mt-2"><b>{courseName}</b></h3>
                                <span class="mt-1 clearfix">{courseID}</span><br />
                                <h5 className="mt-2">Conducted by <b>{teacher.firstName} {teacher.lastName}</b></h5>
                                <h6 className="mt-2">({teacher.email})</h6><br />
                                <h5 className="mt-2">On every {timeSlot}</h5><br />
                                <div class="col-md-0">
                                    <h5>Total Enrollments</h5>
                                    <span class="num">{totalEnrollments}</span>
                                </div>
                                <hr class="line" />
                                <small class="mt-4">{aboutCourse}</small>
                                <div class=" mt-2">
                                    <button className="btn btn-sm btn-info d-sm-none d-md-block" >Show enrolled students</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default MyCoursePage;
