import { Try } from "@mui/icons-material";
import axios from "axios";


class Repository {


  jwt = "";

  #BaseUrl = "";

  instance = undefined;

  constructor() {
    this.#BaseUrl = "http://localhost:8095/api";
    this.jwt = localStorage.getItem('jwt');


    this.instance = axios.create({
      headers: {
        "auth-token": `Bearer ${this.jwt}`
      }
    });
  }


  async signupUser(user) {
    try {
      const res = await this.instance.post(`${this.#BaseUrl}/v1/signup`, user);
      return res.data;
    } catch (err) {
      throw err;
    }
  }


  async loginUser(user) {
    try {
      const res = await this.instance.post(`${this.#BaseUrl}/login/user`, user);
      return res.data;
    } catch (err) {
      throw err;
    }
  }

  async showAllCourses() {

    try {

      const res = await this.instance.post(`${this.#BaseUrl}/user/courses`);
      return res.data;
    } catch (err) {
      throw err;
    }
  }

  async getStuentProfile(currentUser) {

    try {

      const res = await this.instance.get(`${this.#BaseUrl}/student/profile/${currentUser}`);
      return res.data;
    } catch (err) {
      throw err;

    }

  }

  async getTeacherProfile(currentUser) {

    try {

      const res = await this.instance.get(`${this.#BaseUrl}/teacher/profile/${currentUser}`);
      return res.data;
    } catch (err) {
      throw err;

    }

  }

  async getAllStudents() {

    try {

      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allStudents`);
      return res.data;
    } catch (err) {
      throw err;

    }

  }

  async getAllTeachers() {

    try {

      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allTeachers`);
      return res.data;
    } catch (err) {
      throw err;

    }
  }

  async getAllUsers(){
    try {
      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allUsers`);
      return res.data;
    }catch (err) {
      throw err;
    }
  }

  async getStudentsCount() {
    try {
      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allStudents`);
      return res.data.length;
    } catch (err) {
      throw err;
    }
  }

  async getTeachersCount() {
    try {
      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allTeachers`);
      return res.data.length;
    } catch (err) {
      throw err;
    }
  }

  async getTotalCount() {
    const studentsCount = await this.getStudentsCount();
    const teachersCount = await this.getTeachersCount();
    return studentsCount + teachersCount;
  }

  async getTotalCourses() {
    try {
      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allCourses`);
      return res.data.length;
    } catch (err) {
      throw err;
    }
  }

  async getAllCourses() {
    try {
      const res = await this.instance.get(`${this.#BaseUrl}/managementTeam/allCourses`);
      return res.data;
    } catch (err) {
      throw err;
    }
  }

  async logoutUser() {
    try{
      const res = await this.instance.get(`${this.#BaseUrl}/logout/user`);
      return res.data;
    }catch(err) {
      throw err;
    }
  }


}

export default new Repository;
