import React from "react";
import '../CourseDetails.css';

export const CourseDetails = ({ courseName, aboutCourse, teacher, enrollments }) => {

    const handleShowMore = () => {
        // Pass necessary props to CourseDetails component
        return <CourseDetails courseName={courseName} aboutCourse={aboutCourse} teacher={teacher} enrollments={10} />;
      };

    return (
        <div className="Course-details" >
            <div class="container d-flex justify-content-center">
                <div class="c-card p-3 py-4" style={{ marginTop: "8vw" }}>
                    <div class="text-center">
                        <h3 class="mt-2"><b>{courseName}</b></h3>
                        <span class="mt-1 clearfix">courseID</span><br />
                        <h5 className="mt-2">Conducted by {teacher}</h5><br />
                        <div class="col-md-0">
                            <h5>Total Enrollments</h5>
                            <span class="num">{enrollments}</span>
                        </div>
                        <hr class="line" />
                        <small class="mt-4">{aboutCourse}</small>
                        <div class="profile mt-5">
                            <button class="profile_button px-5">Enroll me</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
