import { Link } from 'react-router-dom';
import CourseImage from '../assets/img/courseImg.jpg'
import { CourseDetails } from './CourseDetails';

export const Course = ({ courseID, courseName, aboutCourse, teacher , timeSlot}) => {

  const handleShowMore = () => {
    // Pass necessary props to CourseDetails component
    return <CourseDetails courseName={courseName} aboutCourse={aboutCourse} teacher={teacher} enrollments={10} />;
  };

  return (
    <div className="card" style={{ width: '100%', height: '100%' }}>
      <img src={CourseImage} className="card-img-top" alt="image.." style={{ width: '100%', height: '9vw' }} />
      <div className="card-body">
        <h5 className="card-title" id={`card-title-${courseID}`}>{courseName}</h5>
        <p className="card-text" style={{ color: "black" }}>{aboutCourse}</p>
        <div style={{ display: 'flex' }}>

            <Link to= "/course-details"className="btn btn-sm btn-info d-sm-none d-md-block">Show More</Link>
        </div>
      </div>
    </div>
  );
}
