import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import Repository from '../services/Repository';

const AdminDashboardNavBar = () => {

    const history = useHistory();


    const currentUser = localStorage.getItem('currentUser');
    const token = localStorage.getItem('jwt')

    const handleLogout = () => {

        Repository.logoutUser(token)
            .then(data => {
                console.log(data);
            })
            .catch(error => {
                throw error;
            });

        history.push('/');
    }

    useEffect(() => { }, [handleLogout]);

    return (
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark" style={{ height: '10vh', position: 'fixed', top: '0', left: '0', right: '0' }}>
            <a className="navbar-brand" href="#" style={{ fontSize: '30px' }}><Link to="/dashboard" style={{ textDecoration: 'none', color: 'inherit' }}>Dashboard</Link></a>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
            </button>

            <div className="collapse navbar-collapse" id="navbarSupportedContent">

                <ul className="navbar-nav mr-auto">
                    <li className="nav-item">
                        <Link to="/dash-home" className="nav-link" style={{ fontSize: '20px' }}>Home</Link>
                    </li>
                </ul>

                <ul classNameName="navbar-nav ml-auto">




                    {currentUser && <li className="nav-item" style={{ marginTop: '15px' }}>
                        <a className="nav-link" href="#">
                            <span style={{ fontSize: '20px' }}>Hello, {currentUser}</span>
                            <a className="nav-link" style={{ fontSize: '20px', color: "red", marginLeft: '10px' }} onClick={handleLogout}>Logout</a>
                        </a>
                    </li>}


                </ul>
            </div>
        </nav>);

}

export default AdminDashboardNavBar;
