import React from "react";
import { useState } from "react";
import { Link } from 'react-router-dom';
import '../App.css';

export const CreateCourse = () => {

    const [courseID, setCourseID] = useState("");
    const [courseName, setCourseName] = useState("");
    const [aboutCourse, setAboutCourse] = useState("");
    const [timeSlot, setTimeSlot] = useState("");
    const [teacherUserName, setTeacherUserName] = useState("");
    const [role, setRole] = useState("");
    const [userID, setUserId] = useState("");
    const [errors, setErrors] = useState({});
    const [status, setStatus] = useState("");

    const handleSubmit = (event) => {

            Repository.signupUser(user)
                .then(data => {
                    console.log(data);
                    if (data) {
                        history.push('/signup-success')
                    }
                })
                .catch(error => {

                    if (error.response && error.response.status === 409) {
                        setStatus("Username is already taken, please choose another username.");
                    } else {

                        throw error;
                    }
                });


        event.preventDefault();

    }

    return(

            <div className="App">
            <div className="auth-form-container" >

                <h1>CReate Course</h1><br />
                <form className="register-form" onSubmit={handleSubmit}>
                    <label htmlFor="courseID">Course ID</label>
                    <input value={courseID} onChange={(e) => setCourseID(e.target.value)} name="courseID" id="courseID" placeholder="Course ID" />

                    <label htmlFor="courseName">Course Name</label>
                    <input value={courseName} onChange={(e) => setCourseName(e.target.value)} name="courseName" id="courseName" placeholder="Course Name" />


                    <label htmlFor="aboutCourse">About Course</label>
                    <input value={aboutCourse} onChange={(e) => setAboutCourse(e.target.value)} placeholder="youremail@gmail.com" id="email" name="email" />


                    <label htmlFor="password">password</label>
                    <input value={password} onChange={(e) => setPassword(e.target.value)} type="password" placeholder="******" id="password" name="password" />

                    <label htmlFor="role">Role</label>
                    <select value={role} onChange={e => setRole(e.target.value)}>
                        <option placeholder="Select Role"> </option>
                        <option value="Student">Student</option>
                        <option value="Teacher">Teacher</option>
                    </select>

                    <label htmlFor="userID">UserID</label>
                    <input value={userID} onChange={(e) => setUserId(e.target.value)} placeholder="UserID" id="userID" name="userID" />



                    <button className="registerbtn" type="Submit" onClick={handleSubmit}><b>Register</b></button>
                </form>
                <br />
                <h4>Already have an account?</h4>
                <Link to="/login" className="link-button"><h5>Login here</h5></Link>

            </div>
        </div>

    );
}
