import React from "react";
import '../CourseDetails.css';
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck } from "@fortawesome/free-solid-svg-icons";
import { useLocation } from 'react-router-dom';

export const CourseDetails = ({ courseID, courseName, aboutCourse, teacher, enrollments }) => {

    // const location = useLocation();
    // const name = new URLSearchParams(location.search).get('name');
    // const age = new URLSearchParams(location.search).get('age');


    const [enrolled, setEnrolled] = useState(false);
    const history = useHistory();

    const handleEnrollClick = () => {

        const currentUser = localStorage.getItem("currentUser");

        if (currentUser) {
            setEnrolled(true);
        } else {

            history.push("/login");
        }
    };

    return (
        <div className="Course-details" >
            <div className="container d-flex justify-content-center">
                <div className="c-card p-3 py-4" style={{ marginTop: "8vw" }}>
                    <div className="text-center">
                        <h3 className="mt-2"><b>{courseName}</b></h3>
                        <span className="mt-1 clearfix">{courseID}</span><br />
                        <h5 className="mt-2">Conducted by {teacher}</h5><br />
                        <div className="col-md-0">
                            <h5>Total Enrollments</h5>
                            <span className="num">{enrollments}</span>
                        </div>
                        <hr className="line" />
                        <small className="mt-4">{aboutCourse}</small>
                        <div className="profile mt-5">
                            {enrolled ? (
                                <button id="enrolled-button" className="profile_button px-5" disabled>
                                    <FontAwesomeIcon icon={faCheck} className="mr-2" />
                                    Enrolled
                                </button>
                            ) : (
                                <button className="profile_button px-5" onClick={handleEnrollClick}>
                                    Enroll me
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
