import React from "react";
import { useState, useEffect } from "react";
import Repository from "../../services/Repository";
import AdminDashboardNavBar from "../../components/AdminDashboardNavBar";


const AllCoursesPage = () => {

    const [courses, setCourses] = useState([])
    const [titile, setTitle] = useState("")
    const token = localStorage.getItem('jwt')
    const [totalEnrollments, setTotalEnrollments] = useState("");

    useEffect(() => {
        Repository.getAllCourses(token)
            .then(data => {
                setCourses(data);
                console.log(data);
            })
            .catch(error => {
                throw error;
            });

            Repository.courseEnrollments(courses.courseID)
            .then(data => {
                setTotalEnrollments(data);
            }).catch(error => {
                throw error;
            });

    }, [token, courses.courseID]);

    const setAllCourses = () => {
        Repository.getAllCourses(token)
            .then(data => {
                setCourses(data);
                setTitle("All Courses Details");
                console.log(data);
            })
            .catch(error => {
                throw error;
            });
    };


    return (
        <div>
            <AdminDashboardNavBar />
            <hr />

            <div style={{ marginTop: "5vw" }}>

                <div className="title-home-page"> <h1 className="wow slideInLeft" id="title">{titile}</h1> </div>

            </div>

            <hr />
            <div className="d-flex justify-content-center">
                <button className="btn btn-secondary d-inline-block mr-3" onClick={setAllCourses}>All Courses</button>
            </div>

            <div className="card-container" style={{ display: "flex", justifyContent: "center", alignItems: "center" }}>
                <ol>
                    {courses.map((course, index) => (
                        <li key={index}>
                            <div className="card" id="card-history" style={{ marginTop: "2vw", width: "50vw", float: "none", display: "flex", justifyContent: "center", alignItems: "center", flexDirection: "column", boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)' }}>

                                <div className="student-details" style={{ marginTop: "2vw", marginBottom: "2vw" }}>
                                    <ul>
                                        <li><b>Course ID:</b> {course.courseID}</li>
                                        <li><b>Course Name:</b> {course.courseName}</li>
                                        <li><b>Teacher:</b> {course.teacher.firstName} {course.teacher.lastName} ({course.teacher.username})</li>
                                        <li><b>Time slot:</b> {course.timeSlot}</li>
                                        <li><b>Description:</b> {course.aboutCourse}</li>
                                        <li><b>Total Enrollments</b> {totalEnrollments}</li>
                                    </ul>
                                </div>
                            </div>
                        </li>
                    ))}
                </ol>


            </div>

        </div>
    );
}

export default AllCoursesPage;
