import React from "react";
import '../CourseDetails.css';
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheck } from "@fortawesome/free-solid-svg-icons";


export const CourseDetails = ({ courseID, courseName, aboutCourse, teacher , enrollments}) => {

    const location = useLocation();
  const name = new URLSearchParams(location.search).get('name');
  const age = new URLSearchParams(location.search).get('age');


    const [enrolled, setEnrolled] = useState(false); 
    const history = useHistory(); 

    const handleEnrollClick = () => {

        const currentUser = localStorage.getItem("currentUser");

        if (currentUser) {
            setEnrolled(true);
        } else {

            history.push("/login");
        }
    };

    return (
        <div className="Course-details" >
            <div class="container d-flex justify-content-center">
                <div class="c-card p-3 py-4" style={{ marginTop: "8vw" }}>
                    <div class="text-center">
                        <h3 class="mt-2"><b>{courseName}</b></h3>
                        <span class="mt-1 clearfix">{courseID}</span><br />
                        <h5 className="mt-2">Conducted by {teacher}</h5><br />
                        <div class="col-md-0">
                            <h5>Total Enrollments</h5>
                            <span class="num">{enrollments}</span>
                        </div>
                        <hr class="line" />
                        <small class="mt-4">{aboutCourse}</small>
                        <div class="profile mt-5">
                        {enrolled ? (
                                <button id ="enrolled-button" class="profile_button px-5" disabled>
                                    <FontAwesomeIcon icon={faCheck} className="mr-2" />
                                    Enrolled
                                </button>
                            ) : (
                                <button class="profile_button px-5" onClick={handleEnrollClick}>
                                    Enroll me
                                </button>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
