import CourseImage from '../assets/img/courseImg.jpg'
import { useHistory } from 'react-router-dom';



export const Course = ({ id,courseID, courseName, timeSlot, aboutCourse, teacher, totalEnrollments}) => {



  // const handleShowMore = () => {
  //   // Pass necessary props to CourseDetails component
  //   return <CourseDetails courseID={courseID} courseName={courseName} aboutCourse={aboutCourse} teacher={teacher} enrollments={10} />;
  // };

  const history = useHistory();

  const handleShowMore= () => {

    const courseDetails = {
            "courseID" : courseID,
            "courseName" : courseName,
            "aboutCourse" : aboutCourse,
            "teacherFirstName" : teacher.teacherFirstName,
            "timeSlot" : timeSlot,
            "teacherLastName" : teacher.teacherLastName,
            "teacherEmail" : teacher.teacherEmail,
            "teacherUserName" : teacher.teacherUserName,
            "totalEnrollments" : totalEnrollments
    };

    const encodedCourseDetails = encodeURIComponent(JSON.stringify(courseDetails));
    history.push(`/course-details?courseDetails=${encodedCourseDetails}`);
  }

  return (
    <div className="card" style={{ width: '100%', height: '100%' }}>
      <img src={CourseImage} className="card-img-top" alt="image.." style={{ width: '100%', height: '9vw' }} />
      <div className="card-body">
        <h5 className="card-title" id={`card-title-${courseID}`}>{courseName}</h5>
        <p className="card-text" style={{ color: "black" }}>{aboutCourse}</p>
        <div style={{ display: 'flex' }}>

            <button onClick={handleShowMore} className="btn btn-sm btn-info d-sm-none d-md-block">Show More</button>
        </div>
      </div>
    </div>
  );
}
